class Post extends Component {

  renderPost = () => {
    const { title, image } = this.props.post;
    return (
      <div className="post-content">         
        {image && this.renderPostImage()}
        <div className="post-info">
          <h3>{title}</h3>
          {this.renderAuthor()}
          <p>{test}</p>
        </div>
      </div>
    );
  }
  renderLoadingState = () => (
    <div className="post-loading">
      Loading...
    </div>
  )
  render() {
    const { post } = this.props;
    return (
      <div className="post-container">
        {post ? this.renderPost() : this.renderLoadingState()}
      </div>
    );
  }
}

export default Post;
